import { Component } from '@angular/core';

import { homeCarouselData } from 'src/data/mainCarouselData';

@Component({
  selector: 'app-main-carousel',
  templateUrl: './main-carousel.component.html',
  styleUrls: ['./main-carousel.component.scss']
})
export class MainCarouselComponent {
  carouselData:any;
  currentSlide=0;
  interval:any;

  ngOnInit(){
    this.carouselData=homeCarouselData;
  }

  autoPlay(){
    setInterval(()=>{
      this.nextSlide();
    },2000)
  }

  nextSlide(){
    this.currentSlide=(this.currentSlide+1)% this.carouselData.length;
  }



}
<div class="relative">

    <div class="carousel-container overflow-x-hidden">
        <div class="carousel" [style.transform]="'translateX(-' + (currentSlide * 100) + '%)'">
            <ng-container *ngFor="let item of carouselData; let i = index">
              <!-- Your content for each item goes here -->
              <a [routerLink]="item.path" class="carousel-item">
                <img [src]="item.image" alt="carousel image">
              </a>
            </ng-container>
          </div>

    </div>
</div>
