import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-home-product-card',
  templateUrl: './home-product-card.component.html',
  styleUrls: ['./home-product-card.component.scss']
})
export class HomeProductCardComponent {
  @Input() product:any;



}
<div
  class="box hover:scale-105 transition-all duration-500 cursor-pointer \ flex flex-col items-center rounded-lg overflow-hidden w-[15rem] mx-3"
>
  <div class="h-[13rem] w-[10rem]">
    <img class="object-cover object-top w-full h-full" [src]="product.imageUrl" alt="" />
  </div>

  <div class="p-4">
    <h3 class="font-medium text-gray-900 text-sm">
      {{ product.brand }}
    </h3>
    <p class="mt-2 text-gray-500">
      {{ product.title }}
    </p>
  </div>
</div>
